const item = $input.first();
const body = item.json.body || {};
const intent = body.intent || {};
const runId = body.runId || '';
const action = (intent.action || 'unknown').toLowerCase();
const site = (intent.site || '').toLowerCase();
const params = intent.params || {};

let status = [];
if (action === 'book_train' || (action === 'search_train') || site === 'irctc') {
  status = [
    { status: 'running', message: 'Opening IRCTC…', detail: 'irctc' },
    { status: 'running', message: 'Filling from, to and date…', detail: JSON.stringify(params) },
    { status: 'success', message: 'Demo step done. Add Playwright node for real browser control.', detail: '' }
  ];
} else if (action === 'book_flight' || action === 'search_flight' || site === 'goibibo') {
  status = [
    { status: 'running', message: 'Opening Goibibo…', detail: 'goibibo' },
    { status: 'running', message: 'Filling flight search…', detail: JSON.stringify(params) },
    { status: 'success', message: 'Demo step done. Add Playwright node for real browser control.', detail: '' }
  ];
} else {
  status = [{ status: 'success', message: 'Intent received (demo). Add Playwright node for automation.', detail: action }];
}

return { json: { runId, status } };
